import React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  TextField,
  Button,
  MenuItem,
  Card,
  CardContent,
  Paper,
  FormControl,
  InputLabel,
  Select,
  Pagination,
} from "@mui/material";
import axios from "axios";

function HOME() {
  const [loanAmount, setLoanAmount] = useState("");
  const [interestRate, setInterestRate] = useState("");
  const [loanTenure, setLoanTenure] = useState("");
  const [emi, setEmi] = useState(null);
  const [amortizationSchedule, setAmortizationSchedule] = useState([]);
  const [exchangeRates, setExchangeRates] = useState({});
  const [selectedCurrency, setSelectedCurrency] = useState("USD");
  const [entriesPerPage, setEntriesPerPage] = useState(10);
  const [page, setPage] = useState(1);

  const totalPages = Math.ceil(amortizationSchedule.length / entriesPerPage);

  useEffect(() => {
    axios
      .get(
        "https://v6.exchangerate-api.com/v6/77e571783ee71eebfe494ac5/latest/INR"
      )
      .then((res) => setExchangeRates(res.data.conversion_rates))
      .catch((err) => console.error("Error fetching exchange rates:", err));
  }, []);

  const calculateEMI = () => {
    const principal = parseFloat(loanAmount);
    const rate = parseFloat(interestRate) / 12 / 100;
    const tenure = parseFloat(loanTenure) * 12;
    const emiAmount =
      (principal * rate * Math.pow(1 + rate, tenure)) /
      (Math.pow(1 + rate, tenure) - 1);

    setEmi(emiAmount.toFixed(2));

    let balance = principal;
    let schedule = [];
    for (let i = 1; i <= tenure; i++) {
      let interestPayment = balance * rate;
      let principalPayment = emiAmount - interestPayment;
      balance -= principalPayment;

      schedule.push({
        month: i,
        emi: emiAmount,
        principal: principalPayment,
        interest: interestPayment,
        balance: balance,
      });
    }
    setAmortizationSchedule(schedule);
    setPage(1); 
  };

  const handleCurrencyChange = (e) => {
    setSelectedCurrency(e.target.value);
  };

  const handleChangeEntries = (e) => {
    setEntriesPerPage(parseInt(e.target.value));
    setPage(1);
  };

  const handlePageChange = (_, value) => {
    setPage(value);
  };

  const conversionRate = exchangeRates[selectedCurrency] || 1;

  const paginatedEntries = amortizationSchedule.slice(
    (page - 1) * entriesPerPage,
    page * entriesPerPage
  );

  return (
    <Card sx={{ maxWidth: 1000, margin: "40px auto", padding: "20px" }}>
      <CardContent>
        <Typography variant="h4" gutterBottom>
          Loan EMI Calculator
        </Typography>

        <Box sx={{ display: "flex", gap: 2, flexWrap: "wrap", mb: 2 }}>
          <TextField
            label="Loan Amount"
            variant="outlined"
            value={loanAmount}
            onChange={(e) => setLoanAmount(e.target.value)}
          />
          <TextField
            label="Interest Rate (%)"
            variant="outlined"
            value={interestRate}
            onChange={(e) => setInterestRate(e.target.value)}
          />
          <TextField
            label="Loan Tenure (years)"
            variant="outlined"
            value={loanTenure}
            onChange={(e) => setLoanTenure(e.target.value)}
          />
          <TextField
            select
            label="Currency"
            value={selectedCurrency}
            onChange={handleCurrencyChange}
          >
            {Object.keys(exchangeRates).map((currency) => (
              <MenuItem key={currency} value={currency}>
                {currency}
              </MenuItem>
            ))}
          </TextField>
        </Box>

        <Button variant="contained" onClick={calculateEMI}>
          Calculate EMI
        </Button>

        {emi && (
          <Box sx={{ mt: 3 }}>
            <Typography variant="h6">
              Estimated EMI in INR: ₹{parseFloat(emi).toFixed(2)}
            </Typography>
            <Typography variant="h6">
              Estimated EMI in {selectedCurrency}: {selectedCurrency}{" "}
              {(emi * conversionRate).toFixed(2)}
            </Typography>
          </Box>
        )}

        {amortizationSchedule.length > 0 && (
          <Box sx={{ mt: 5 }}>
            <Typography variant="h5" gutterBottom>
              Amortization Schedule ({selectedCurrency})
            </Typography>

            <Paper sx={{ padding: 2 }}>
              {/* Header Row */}
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  borderBottom: "2px solid #000",
                  fontWeight: "bold",
                  paddingY: 1,
                }}
              >
                <Typography>Month</Typography>
                <Typography>EMI</Typography>
                <Typography>Principal</Typography>
                <Typography>Interest</Typography>
                <Typography>Balance</Typography>
              </Box>

              {/* Table Entries */}
              {paginatedEntries.map((row) => (
                <Box
                  key={row.month}
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    borderBottom: "1px solid #ddd",
                    paddingY: 1,
                  }}
                >
                  <Typography>{row.month}</Typography>
                  <Typography>
                    {selectedCurrency} {(row.emi * conversionRate).toFixed(2)}
                  </Typography>
                  <Typography>
                    {selectedCurrency}{" "}
                    {(row.principal * conversionRate).toFixed(2)}
                  </Typography>
                  <Typography>
                    {selectedCurrency}{" "}
                    {(row.interest * conversionRate).toFixed(2)}
                  </Typography>
                  <Typography>
                    {selectedCurrency}{" "}
                    {(row.balance * conversionRate).toFixed(2)}
                  </Typography>
                </Box>
              ))}
            </Paper>

            <Box
              sx={{
                display: "flex",
                justifyContent: "space-between",
                mt: 3,
                flexWrap: "wrap",
                gap: 2,
              }}
            >
              <Typography variant="h6">
                Total Entries: {amortizationSchedule.length}
              </Typography>
              <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
                <FormControl size="small">
                  <InputLabel>Entries</InputLabel>
                  <Select
                    value={entriesPerPage}
                    label="Entries"
                    onChange={handleChangeEntries}
                  >
                    {[10, 25, 50, 100].map((num) => (
                      <MenuItem key={num} value={num}>
                        {num}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <Pagination
                  count={totalPages}
                  page={page}
                  onChange={handlePageChange}
                  sx={{ backgroundColor: "#fff", borderRadius: 1 }}
                />
              </Box>
            </Box>
          </Box>
        )}
      </CardContent>
    </Card>
  );
}

export default HOME;
