import React, { useState } from "react";
import FloatingLabelInput from "../COMPONENTS/FloatingLabelInput.js";

function HOME() {
  const [loanAmount, setLoanAmount] = useState("");
  const [interestRate, setInterestRate] = useState("");
  const [loanTenure, setLoanTenure] = useState("");
  const [emi, setEmi] = useState(null);
  const [amortizationSchedule, setAmortizationSchedule] = useState([]);

  const calculateEMI = () => {
    const principal = parseFloat(loanAmount);
    const rate = parseFloat(interestRate) / 12 / 100;
    const tenure = parseFloat(loanTenure) * 12;
    const emiAmount =
      (principal * rate * Math.pow(1 + rate, tenure)) /
      (Math.pow(1 + rate, tenure) - 1);

    setEmi(emiAmount.toFixed(2));

    let balance = principal;
    let schedule = [];
    for (let i = 1; i <= tenure; i++) {
      let interestPayment = balance * rate;
      let principalPayment = emiAmount - interestPayment;
      balance -= principalPayment;

      schedule.push({
        month: i,
        emi: emiAmount.toFixed(2),
        principal: principalPayment.toFixed(2),
        interest: interestPayment.toFixed(2),
        balance: balance.toFixed(2),
      });
    }
    setAmortizationSchedule(schedule);
  };

  return (
    <div style={{ width: "300px", margin: "50px auto" }}>
      <h2>Loan EMI Calculator</h2>

      <FloatingLabelInput
        label="Loan Amount"
        value={loanAmount}
        onChange={(e) => setLoanAmount(e.target.value)}
      />
      <FloatingLabelInput
        label="Interest Rate (%)"
        value={interestRate}
        onChange={(e) => setInterestRate(e.target.value)}
      />
      <FloatingLabelInput
        label="Loan Tenure (years)"
        value={loanTenure}
        onChange={(e) => setLoanTenure(e.target.value)}
      />

      <button onClick={calculateEMI}>Calculate EMI</button>

      {emi && <h3>Estimated EMI: â‚¹{emi}</h3>}

      {amortizationSchedule.length > 0 && (
        <table border="1">
          <thead>
            <tr>
              <th>Month</th>
              <th>EMI</th>
              <th>Principal</th>
              <th>Interest</th>
              <th>Balance</th>
            </tr>
          </thead>
          <tbody>
            {amortizationSchedule.map((row) => (
              <tr key={row.month}>
                <td>{row.month}</td>
                <td>{row.emi}</td>
                <td>{row.principal}</td>
                <td>{row.interest}</td>
                <td>{row.balance}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default HOME;
