import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Switch from "@mui/material/Switch";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import { BrowserRouter as Router, Link, Route, Routes } from "react-router-dom";
import "./App.css";

// Example Components for Navigation
function Home() {
  return <h1>Home Page</h1>;
}

function About() {
  return <h1>About Page</h1>;
}

function ExchangeRates() {
  return <h1>Exchange Rates (Live) Page</h1>;
}

function ErrorPage() {
  return <h1>Error Page</h1>;
}

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const theme = createTheme({
    palette: {
      mode: darkMode ? "dark" : "light",
      primary: {
        main: darkMode ? "#90caf9" : "#1976d2",
      },
    },
  });

  const handleThemeChange = () => {
    setDarkMode(!darkMode);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <div className="App">
          {/* Navbar */}
          <nav
            style={{
              backgroundColor: darkMode ? "#333" : "#1976d2", 
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <h2 className="title">Loan Calculator</h2>
            <div className="links">
              <Link to="/" style={{ textDecoration: 'none' }}>
                <Button variant="text" sx={{ color: "#ffffff" }}>
                  home
                </Button>
              </Link>
              <Link to="/exchange-rates" style={{ textDecoration: 'none' }}>
                <Button variant="text" sx={{ color: "#ffffff" }}>
                  exchange rates (live)
                </Button>
              </Link>
              <Link to="/about" style={{ textDecoration: 'none' }}>
                <Button variant="text" sx={{ color: "#ffffff" }}>
                  about
                </Button>
              </Link>
              <Link to="/error" style={{ textDecoration: 'none' }}>
                <Button variant="text" sx={{ color: "#ffffff" }}>
                  error page
                </Button>
              </Link>
              <Switch checked={darkMode} onChange={handleThemeChange} />
            </div>
          </nav>

          {/* Routes for different pages */}
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/exchange-rates" element={<ExchangeRates />} />
            <Route path="/error" element={<ErrorPage />} />
          </Routes>

          {/* Other components */}
          <div className="Input">
            <TextField label="Loan Amount" variant="outlined" />
            <TextField label="Interest Rate (%)" variant="outlined" />
            <TextField label="Loan Tenure (Years)" variant="outlined" />
          </div>

          <Button className="button" variant="contained">
            calculate
          </Button>
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
