import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import axios from "axios";

function HOME() {
  const [loanAmount, setLoanAmount] = useState("");
  const [interestRate, setInterestRate] = useState("");
  const [loanTenure, setLoanTenure] = useState("");
  const [emi, setEmi] = useState(null);
  const [amortizationSchedule, setAmortizationSchedule] = useState([]);
  const [exchangeRates, setExchangeRates] = useState({});
  const [selectedCurrency, setSelectedCurrency] = useState("USD");

  // Fetch exchange rates once on mount
  useEffect(() => {
    axios
      .get("https://v6.exchangerate-api.com/v6/77e571783ee71eebfe494ac5/latest/INR")
      .then((res) => setExchangeRates(res.data.conversion_rates))
      .catch((err) => console.error("Error fetching exchange rates:", err));
  }, []);

  const calculateEMI = () => {
    const principal = parseFloat(loanAmount);
    const rate = parseFloat(interestRate) / 12 / 100;
    const tenure = parseFloat(loanTenure) * 12;
    const emiAmount =
      (principal * rate * Math.pow(1 + rate, tenure)) /
      (Math.pow(1 + rate, tenure) - 1);

    setEmi(emiAmount.toFixed(2));

    let balance = principal;
    let schedule = [];
    for (let i = 1; i <= tenure; i++) {
      let interestPayment = balance * rate;
      let principalPayment = emiAmount - interestPayment;
      balance -= principalPayment;

      schedule.push({
        month: i,
        emi: emiAmount.toFixed(2),
        principal: principalPayment.toFixed(2),
        interest: interestPayment.toFixed(2),
        balance: balance.toFixed(2),
      });
    }
    setAmortizationSchedule(schedule);
  };

  const convertedEmi = emi && exchangeRates[selectedCurrency]
    ? (emi * exchangeRates[selectedCurrency]).toFixed(2)
    : null;

  return (
    <div style={{ maxWidth: "900px", margin: "50px auto" }}>
      <h2>Loan EMI Calculator</h2>

      <div style={{ display: "flex", gap: "20px", marginBottom: "20px" }}>
        <TextField
          label="Loan Amount"
          variant="outlined"
          value={loanAmount}
          onChange={(e) => setLoanAmount(e.target.value)}
        />
        <TextField
          label="Interest Rate (%)"
          variant="outlined"
          value={interestRate}
          onChange={(e) => setInterestRate(e.target.value)}
        />
        <TextField
          label="Loan Tenure (years)"
          variant="outlined"
          value={loanTenure}
          onChange={(e) => setLoanTenure(e.target.value)}
        />
        <TextField
          select
          label="Currency"
          value={selectedCurrency}
          onChange={(e) => setSelectedCurrency(e.target.value)}
        >
          {Object.keys(exchangeRates).map((currency) => (
            <MenuItem key={currency} value={currency}>
              {currency}
            </MenuItem>
          ))}
        </TextField>
      </div>

      <Button variant="contained" onClick={calculateEMI}>
        Calculate EMI
      </Button>

      {emi && (
        <div style={{ marginTop: "20px" }}>
          <h3>Estimated EMI in INR: ₹{emi}</h3>
          {convertedEmi && (
            <h3>
              Estimated EMI in {selectedCurrency}: {selectedCurrency} {convertedEmi}
            </h3>
          )}
        </div>
      )}

      {amortizationSchedule.length > 0 && (
        <div style={{ overflowX: "auto", marginTop: "20px" }}>
          <table border="1" cellPadding="10" style={{ minWidth: "600px" }}>
            <thead>
              <tr>
                <th>Month</th>
                <th>EMI</th>
                <th>Principal</th>
                <th>Interest</th>
                <th>Balance</th>
              </tr>
            </thead>
            <tbody>
              {amortizationSchedule.slice(0, 10).map((row) => (
                <tr key={row.month}>
                  <td>{row.month}</td>
                  <td>{row.emi}</td>
                  <td>{row.principal}</td>
                  <td>{row.interest}</td>
                  <td>{row.balance}</td>
                </tr>
              ))}
            </tbody>
          </table>
          {/* Pagination buttons can be added here */}
        </div>
      )}
    </div>
  );
}

export default HOME;
