import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Switch from "@mui/material/Switch";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import "./App.css";
import { colors } from "@mui/material";

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const theme = createTheme({
    palette: {
      mode: darkMode ? "dark" : "light",
      primary: {
        main: darkMode ? "#90caf9" : "#1976d2",
      },
    },
  });

  const handleThemeChange = () => {
    setDarkMode(!darkMode);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline /> {/* Apply global styles */}
      <div className="App">
        <nav>
          <h2 className="title">Loan Calculator</h2>
          <div className="links">
            <a href="/" >

            <Button variant="text" sx={{color: '#ffffff'}}>home</Button>
            </a>
            <Button variant="text" sx={{color: '#ffffff'}}>exchange rates (live)</Button>
            <Button variant="text" sx={{color: '#ffffff'}}>about</Button>
            <Button variant="text" sx={{color: '#ffffff'}}>error page</Button>
            <Switch checked={darkMode} onChange={handleThemeChange} />
          </div>
        </nav>

        <div className="Input">
          <TextField label="Loan Amount" variant="outlined" />
          <TextField label="Interest Rate (%)" variant="outlined" />
          <TextField label="Loan Tenure (Years)" variant="outlined" />
        </div>

        <Button className="button" variant="contained">
          calculate
        </Button>
      </div>
    </ThemeProvider>
  );
}

export default App;
