import React, { useState } from "react";
// import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Switch from "@mui/material/Switch";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import { BrowserRouter as Router, Link, Route, Routes } from "react-router-dom";
import "./App.css";
import HOME from "./COMPONENTS/HOME";
import ABOUT from "./COMPONENTS/ABOUT";
import EXCHANGE from "./COMPONENTS/EXCHANGE ";

// Example Components for Navigation

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const theme = createTheme({
    palette: {
      mode: darkMode ? "dark" : "light",
      primary: {
        main: darkMode ? "#90caf9" : "#1976d2",
      },
    },
  });

  const handleThemeChange = () => {
    setDarkMode(!darkMode);
  };

  useEffect(() => {
    fetch(`https://api.frankfurter.app/latest?from=${base}`)
      .then(res => res.json())
      .then(data => setRates(data.rates))
      .catch(err => console.error("Error fetching exchange rates:", err));
  }, [base]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div className="App">
        <Router>
          <nav
            style={{
              backgroundColor: darkMode ? "#333" : "#1976d2",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <h2 className="title">Loan Calculator</h2>
            <div className="links">
              <Link to="/" style={{ textDecoration: "none", padding: "10px" }}>
                <Button variant="text" sx={{ color: "#ffffff" }}>
                  home
                </Button>
              </Link>
              <Link
                to="/exchange-rates"
                style={{ textDecoration: "none", padding: "10px" }}
              >
                <Button variant="text" sx={{ color: "#ffffff" }}>
                  exchange rates (live)
                </Button>
              </Link>
              <Link
                to="/about"
                style={{ textDecoration: "none", padding: "10px" }}
              >
                <Button variant="text" sx={{ color: "#ffffff" }}>
                  about
                </Button>
              </Link>

              <Switch checked={darkMode} onChange={handleThemeChange} />
            </div>
          </nav>

          <Routes>
            <Route path="/" element={<HOME />} />
            <Route path="/about" element={<ABOUT />} />
            <Route path="/exchange-rates" element={<EXCHANGE />} />
          </Routes>
        </Router>
      </div>
    </ThemeProvider>
  );
}

export default App;
