import React , { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Switch from "@mui/material/Switch";
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from "@mui/material/CssBaseline"; 
import "./App.css";
function App() {
  const [darkMode, setDarkMode] = useState(false);
  const label = { inputProps: { "aria-label": "Switch demo" } };
  const theme = createTheme({
    palette: {
      mode: darkMode ? "dark" : "light",
      primary: {
        main: darkMode ? "#90caf9" : "#1976d2",
      },
    },
  });

  const handleThemeChange = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="App">
      <nav>
        <h2 className="title">Loan Calculator</h2>
        <div className="links">
        <ThemeProvider theme={theme}>
          <Button variant="text" style ={{
            color: '#ffffff'
          }}className="button" >home</Button>
          <Button variant="text">exhcange reates (live)</Button>
          <Button variant="text">about</Button>
          <Button variant="text">error page</Button>
          </ThemeProvider>
          <Switch {...label} defaultUnChecked />
        </div>
      </nav>

      <div className="Input">
        <TextField id="outlined-basic" label="Loan Amount" variant="outlined" />
        <TextField
          id="outlined-basic"
          label="Interest Rate (%)"
          variant="outlined"
        />
        <TextField
          id="outlined-basic"
          label="Loan Tenure (Years)"
          variant="outlined"
        />
      </div>

      <Button className="button" variant="contained">
        calculate
      </Button>
    </div>
  );
}

export default App;
