import React, { useState } from "react";
import "./FloatingLabelInput.css"; // Create this CSS file

function FloatingLabelInput({ label, value, onChange }) {
  return (
    <div className={`floating-label-input ${value ? "has-content" : ""}`}>
      <input
        type="text"
        value={value}
        onChange={onChange}
        required
      />
      <label>{label}</label>
    </div>
  );
}

export default FloatingLabelInput;
