import React, { useState } from "react";
import FloatingLabelInput from "./COMPONENTS/HOME.js";

function App() {
  const [loanAmount, setLoanAmount] = useState("");

  return (
    <div style={{ width: "300px", margin: "50px auto" }}>
      <FloatingLabelInput
        label="Loan Amount"
        value={loanAmount}
        onChange={(e) => setLoanAmount(e.target.value)}
      />
    </div>
  );
}

export default App;
