import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Switch from "@mui/material/Switch";
import { createTheme, ThemeProvider } from '@mui/material/styles';

import "./App.css";

function App() {
  const label = { inputProps: { "aria-label": "Switch demo" } };

  const theme = createTheme({
    palette: {
      primary: {
        main: '#ffffff',
      },
    },
  });

  // State to track active nav button
  const [activePage, setActivePage] = useState("home");

  const navButtons = ["home", "exchange rates (live)", "about", "error page"];

  return (
    <div className="App">
      <nav>
        <h2 className="title">Loan Calculator</h2>
        <div className="links">
          <ThemeProvider theme={theme}>
            {navButtons.map((label) => (
              <Button
                key={label}
                variant="text"
                onClick={() => setActivePage(label)}
                sx={{
                  color: activePage === label ? "#1976d2" : "#ffffff",
                  borderBottom: activePage === label ? "2px solid #1976d2" : "none",
                  fontWeight: activePage === label ? "bold" : "normal",
                  textTransform: "capitalize"
                }}
              >
                {label}
              </Button>
            ))}
          </ThemeProvider>
          <Switch {...label} defaultUnChecked />
        </div>
      </nav>

      <div className="Input">
        <TextField id="loan" label="Loan Amount" variant="outlined" />
        <TextField id="interest" label="Interest Rate (%)" variant="outlined" />
        <TextField id="tenure" label="Loan Tenure (Years)" variant="outlined" />
      </div>

      <Button className="button" variant="contained">
        calculate
      </Button>
    </div>
  );
}

export default App;
